import React, { Component } from 'react';
import Header from './components/Header'
import Footer from './components/Footer'
import Sidebar from './components/Sidebar'
import List from './components/List'
import Channel from './components/Channel'
import ListDesactive from './components/ListDesactive'
import styles from './styles/main.css';
import { BrowserRouter as Router, Route, Link } from 'react-router-dom';

class App extends Component {

  handleClick = () => {
    function clickable(){
      var classname = document.getElementsByClassName("class-static");
      var myFunction = function() {
        var cover = this.getAttribute("data-id"); 
        alert(cover);  


        var element = document.getElementById(cover);
        element.parentNode.removeChild(element);

        function removeElement(elementId) {
          // Removes an element from the document
          var element = document.getElementById(elementId);
          element.parentNode.removeChild(element);
      } 

      };
      Array.from(classname).forEach(function(element) {
        element.addEventListener("click", myFunction);
      });
      alert();

    }


    setTimeout(function(){ clickable();  }, 900);
  }

  constructor(props) {
    super(props);
    this.handleLoad = this.handleLoad.bind(this);
  }

  componentDidMount() {
    window.addEventListener('load', this.handleLoad);
  }

  handleLoad() {
    this.handleClick();
  }


  render() {
    return (
      <Router>
        <div>
          <Header />
          <main className={styles['main']} >
            <Sidebar />
            <div className={styles['main-content']}>
              <Route exact path="/" component={List} />
              <Route exact path="/ListDesactive" component={ListDesactive} />
              <Route exact path="/Channel" component={Channel} />
            </div>
          </main>
          <Footer />
        </div>
      </Router>
    );
  }
}

export default App;
